
public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String browsername="chrome";
		switch (browsername) {
		  case "chrome":
		    System.out.println("chrome");
		    break;
		  case "IE":
		    System.out.println("Tuesday");
		    break;
		  case "firefox":
			    System.out.println("Tuesday");
			    break;
		  default:  System.out.println("Invalid browser");
          break;
	}

	}

}
